];

// Create array with company-page combinations
const results = [];
companies.forEach(company => {
  Object.keys(company.urls).forEach(pageType => {
    results.push({
      company: company.name,
      color: company.color,
      baseUrl: company.baseUrl,
      pageType: pageType,
      url: company.urls[pageType],
      timestamp: new Date().toISOString()
    });
