});

return results.map(item => ({ json: item }));

// Content extraction and analysis
const cheerio = require('cheerio');
const crypto = require('crypto');

// Get the HTML content
const html = $input.item.json.data || '';
const company = $input.item.json.company;
const pageType = $input.item.json.pageType;
const baseUrl = $input.item.json.baseUrl;
const currentUrl = $input.item.json.url;

if (!html) {
  return [];
}

// Load HTML with cheerio
const $ = cheerio.load(html);

// Content selectors based on page type
const selectors = {
  articles: [
    'article h1, article h2, article h3',
    '.insight-title, .article-title, .publication-title',
    '.news-item h2, .news-item h3',
    '[class*="article"] h2, [class*="insight"] h2',
    '[class*="publication"] h2, [class*="report"] h2',
    '.content-title, .post-title',
    'h2 a[href*="/insights/"], h3 a[href*="/insights/"]'
  ],
  events: [
    '.event-title, .event-name, .event-heading',
    '[class*="event"] h2, [class*="event"] h3',
    '.upcoming-events h2, .upcoming-events h3',
    '.conference-title, .webinar-title',
    '[class*="webinar"] h2, [class*="conference"] h2',
    'h2 a[href*="/events/"], h3 a[href*="/events/"]',
    '.calendar-event h2, .calendar-event h3'
  ],
  careers: [
    '.job-title, .position-title, .career-title',
    '[class*="job"] h2, [class*="career"] h2',
    '.vacancy-title, .opening-title',
    '[class*="position"] h2, [class*="role"] h2',
    'h2 a[href*="/careers/"], h3 a[href*="/careers/"]',
    '.job-listing h2, .job-listing h3'
  ],
  main: [
    '.news h2, .news h3, .latest h2, .latest h3',
    '.announcement-title, .press-release h2',
    '[class*="latest"] h2, [class*="recent"] h2',
    '[class*="news"] h2, [class*="update"] h2',
    '.hero-title, .featured h2',
    'h1, h2:first-of-type'
  ]
};

// Extract content
let content = [];
const currentSelectors = selectors[pageType] || selectors.main;

currentSelectors.forEach(selector => {
  $(selector).each((i, el) => {
    const element = $(el);
    const text = element.text().trim();

    // Skip if text is too short or contains common non-content
    if (!text || text.length < 15 || 
        text.toLowerCase().includes('cookie') ||
        text.toLowerCase().includes('newsletter') ||
        text.toLowerCase().includes('subscribe')) {
      return;
    }

    // Find associated link
    let link = null;

    // Check if element itself is a link
    if (element.is('a')) {
      link = element.attr('href');
    } else {
      // Look for link in parent or child elements
      const parentLink = element.closest('a').attr('href');
      const childLink = element.find('a').first().attr('href');
      link = parentLink || childLink;
    }

    // Convert relative URLs to absolute
    if (link) {
      try {
        if (link.startsWith('/')) {
          link = baseUrl + link;
        } else if (link.startsWith('./')) {
          link = baseUrl + link.substring(1);
        } else if (!link.startsWith('http')) {
          link = baseUrl + '/' + link;
        }
      } catch (e) {
        link = null;
      }
    }

    // Create content hash for comparison
    const contentHash = crypto.createHash('md5').update(text + (link || '')).digest('hex');

    // Check for duplicates in current extraction
    if (!content.some(item => item.hash === contentHash)) {
      content.push({
        title: text,
        link: link,
        hash: contentHash,
        extractedAt: new Date().toISOString()
      });
    }
  });
});

// Limit to most relevant content (top 20 items)
content = content.slice(0, 20);

// Create overall page hash
const pageHash = crypto.createHash('md5')
  .update(JSON.stringify(content.map(item => item.hash)))
  .digest('hex');

return [{
  json: {
    company: company,
    pageType: pageType,
    url: currentUrl,
    content: content,
    pageHash: pageHash,
    contentCount: content.length,
    timestamp: new Date().toISOString(),
    color: $input.item.json.color
  }
}];

// Change detection logic
const crypto = require('crypto');

try {
  // Get current data
  const company = $json.company;
  const pageType = $json.pageType;
  const currentPageHash = $json.pageHash;
  const currentContent = $json.content || [];
  const storageKey = `${company}_${pageType}`;

  // Get workflow static data for persistence
  const staticData = $workflow.getStaticData('global');

  // Get previous data
  const previousPageHash = staticData[`${storageKey}_hash`];
  const previousContent = staticData[`${storageKey}_content`] || [];

  console.log(`Checking ${company} - ${pageType}: Previous hash: ${previousPageHash}, Current hash: ${currentPageHash}`);

  // Initialize tracking if first run
  if (!previousPageHash) {
    console.log(`First run for ${storageKey} - storing initial data`);
    staticData[`${storageKey}_hash`] = currentPageHash;
    staticData[`${storageKey}_content`] = currentContent;
    staticData[`${storageKey}_lastCheck`] = new Date().toISOString();
    return []; // No changes on first run
  }

  // Check if content changed
  if (currentPageHash === previousPageHash) {
    console.log(`No changes detected for ${storageKey}`);
    staticData[`${storageKey}_lastCheck`] = new Date().toISOString();
    return []; // No changes
  }

  console.log(`Changes detected for ${storageKey}!`);

  // Find new content items
  const newItems = currentContent.filter(currentItem => 
    !previousContent.some(prevItem => prevItem.hash === currentItem.hash)
  );

  // Update stored data
  staticData[`${storageKey}_hash`] = currentPageHash;
  staticData[`${storageKey}_content`] = currentContent;
  staticData[`${storageKey}_lastCheck`] = new Date().toISOString();

  if (newItems.length > 0) {
    console.log(`Found ${newItems.length} new items for ${storageKey}`);

    return [{
      json: {
        company: company,
        pageType: pageType,
        url: $json.url,
        newItems: newItems,
        totalNewItems: newItems.length,
        color: $json.color,
        timestamp: new Date().toISOString()
      }
    }];
  } else {
    console.log(`Content changed but no new items identified for ${storageKey}`);
    return [];
  }
} catch (error) {
  console.error('Error in change detection:', error.message);
  // Return empty array on error to prevent workflow failure
  return [];
}

// Format Teams message
