  });
}

// Create Teams adaptive card
const teamsMessage = {
  "@type": "MessageCard",
  "@context": "http://schema.org/extensions",
  "themeColor": color,
  "summary": `Nowa zawarto≈õƒá - ${company}`,
  "sections": [{
    "activityTitle": "üö® Wykryto nowƒÖ zawarto≈õƒá!",
    "activitySubtitle": `**${company}** - ${pageTypeLabels[pageType] || pageType}`,
    "activityImage": "https://img.icons8.com/color/96/000000/website.png",
    "facts": facts,
    "markdown": true
  }],
  "potentialAction": [{
    "@type": "OpenUri",
    "name": "üåê Zobacz stronƒô",
    "targets": [{
      "os": "default",
      "uri": url
    }]
  }]
};

return [{
  json: teamsMessage
}];

// Simple error logging without static data access
const company = $input.item.json.company || 'Unknown';
const pageType = $input.item.json.pageType || 'Unknown';
const url = $input.item.json.url || 'Unknown URL';
const statusCode = $input.item.json.statusCode || 'No status';
const error = $input.item.json.error || 'Unknown error';

// Log error details
console.error(`‚ùå Error fetching content:`);
console.error(`   Company: ${company}`);
console.error(`   Page Type: ${pageType}`);
console.error(`   URL: ${url}`);
console.error(`   Status Code: ${statusCode}`);
console.error(`   Error: ${error}`);
console.error(`   Timestamp: ${new Date().toISOString()}`);

// Return the error information for potential further processing
return [{
  json: {
    errorType: 'fetch_failed',
    company: company,
    pageType: pageType,
    url: url,
    statusCode: statusCode,
    error: error,
    timestamp: new Date().toISOString()
  }
}];
